import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState();
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== '';
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;

  const inputChangeHandler = (event) => {
    setEnteredName(event.target.value);
    if (enteredName !== 0){
      setEnteredNameIsValid(true);
    }
  };

  const blurChangeHandler = event => {
    setEnteredNameTouched(true);

    if (enteredName === ''){
      setEnteredNameIsValid(false);
    }
  }

  const formSubmitHandler = (event) => {
    event.preventDefault();

    setEnteredNameTouched(true);

    if (enteredName.trim() === ''){
      setEnteredNameIsValid(false);
      return;
    }

    console.log(enteredName);
    setEnteredName("");
  };



  const invalidInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={invalidInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputChangeHandler}
          onBlur={blurChangeHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && <p className="error-text">Name must not empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
