import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState();
  const [enteredEmail, setEnteredEmail] = useState();
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const enteredNameIsValid = enteredName !== "";
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched && enteredEmailTouched;

  let formIsValid = false;

  if(enteredNameIsValid){
    formIsValid = true;
  }

  const inputNameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const inputEmailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const blurNameChangeHandler = (event) => {
    setEnteredNameTouched(true);
  };
  const blurEmailChangeHandler = (event) => {
    setEnteredEmailTouched(true);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    setEnteredNameTouched(true);
    setEnteredEmailTouched(true);
    if (!enteredNameIsValid) {
      return;
    }

    console.log(enteredName);
    console.log(enteredEmail);
    setEnteredName("");
    setEnteredEmail("");
    setEnteredNameTouched(false);
    setEnteredEmailTouched(true);
  };

  const invalidInputClasses = nameInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={invalidInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={inputNameChangeHandler}
          onBlur={blurNameChangeHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && (
          <p className="error-text">Name must not empty.</p>
        )}
      </div>

      <div className={invalidInputClasses}>
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={inputEmailChangeHandler}
          onBlur={blurEmailChangeHandler}
          value={enteredEmail}
        />
        {nameInputIsInvalid && (
          <p className="error-text">email must not empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
