import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';

const Cart = (props) => {
  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        <CartItem
          item={{ title: item.name, quantity: item.quantity, total: item.totalquantity, price: item.price }}
        />
      </ul>
    </Card>
  );
};

export default Cart;
